import { FC, ReactNode } from 'react';
import { PartialStoryFn, ReactJSXElement } from '@storybook/types';

declare const withThemeProvider: <TTheme>(Provider: FC<{
    children?: ReactNode;
    theme?: TTheme;
}>) => (StoryFn: PartialStoryFn<{
    component: FC;
    storyResult: ReactJSXElement;
    canvasElement: unknown;
}>) => JSX.Element;

type ThemeConfigType<TTheme = any> = {
    name: string;
    color?: string;
    themeObject: TTheme;
};

export { ThemeConfigType, withThemeProvider };
